mapper.select:
SELECT COUNT(id)
FROM users
WHERE id=#{id}

SELECT userno, id, passwd, uname, phone, postal_code, address1, address2, udate, grade
FROM users
WHERE userno = #{userno}

SELECT userno, id, passwd, uname, phone, postal_code, address1, address2, udate, grade
FROM users
WHERE id = #{id}

mapper.update:
UPDATE users 
SET uname=#{uname}, phone=#{phone}, postal_code=#{postal_code}, address1=#{address1}, address2=#{address2}
WHERE userno=#{userno}

mapper.select:
SELECT COUNT(userno) as cnt
FROM users
WHERE id=#{id} AND passwd=#{passwd}

SELECT COUNT(userno) as cnt
FROM users
WHERE userno=#{userno} AND passwd=#{passwd}

mapper.update:
UPDATE users
SET passwd=#{passwd}
WHERE userno=#{userno}
